        [Test]
        public void BasicTest_MusicBrainzJsonInterpretor_FromMusicBrainzReleasesResult()
        {
            dynamic iDynaminac = DynamicJsonConverter.DynamicDeSerialize(_ExempleResponse);
            CancellationToken ct = new CancellationToken();

            IEnumerable<AlbumDescriptor> res = MusicBrainzJsonInterpretor.FromMusicBrainzReleaseResults(iDynaminac, "XzPS7vW.HPHsYemQh0HBUGr8vuU-", ct);
            List<AlbumDescriptor> las = res.ToList();

            las.Should().HaveCount(2);
            AlbumDescriptor ad = las[0];
            ad.Should().NotBeNull();
            ad.Name.Should().Be("In It for the Money");
            ad.Artist.Should().Be("Supergrass");
            ad.TracksNumber.Should().Be(12);
            ad.TrackDescriptors[0].Name.Should().Be("In It for the Money");
            ad.TrackDescriptors[1].Name.Should().Be("Richard III");
            ad.TrackDescriptors[2].Name.Should().Be("Tonight");
            ad.TrackDescriptors[3].Name.Should().Be("Late in the Day");
            ad.HasImage().Should().BeFalse();

            LoadingAlbumDescriptor ad2 = las[1] as LoadingAlbumDescriptor;
            ad2.Should().NotBeNull();
            ad2.HasImage().Should().BeTrue();
            ad2.Images.Should().BeNull();

            ad2.Load();
            ad2.HasImage().Should().BeTrue();
            ad2.Images.Should().NotBeNull();
        }

